import React, { Component } from 'react';
import { Route, Redirect } from 'react-router-dom';

import * as eng from './providers/engine';

import Header from './partials/header';
import Footer from './partials/footer';

import Authenticate from './pages/authenticate/auth';
import Academy from './pages/academy/academy';

export default class SirJava extends Component {
    constructor(props) {
        super(props);
        this.state = {
            msg: '', status: ''
        };
    }

    params = this.props.match.params;
    authPages = ['signin', 'signup', 'forgot'];

    __testToken = () => {
        const action = this.params.action ? this.params.action : '';
        eng.postData('accounts&case=token').then((res) => {
            if (eng.inArray(action, this.authPages) === false) {
                if (res.status === 404 && res.result === 'Invalid $apiToken!!') {
                    this.props.history.push('/logout');
                } else if (res.status === 200) {
                    eng.setStorageJson('userData', res.result);
                }
            }
        });
    }

    render() {
        const action = this.params.action ? this.params.action : '';
        eng.setStorageJson('route', this.params);

        const { status, msg } = this.state;

        return (
            <React.Fragment>
                <Header />
                {eng.alertMsg(status, msg)}
                <Route exact path="/" component={Academy} />
                <Route exact path="/:action" component={Academy} />
                <Footer />
            </React.Fragment>
        );
    }
}
