import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import * as eng from '../../providers/engine';
import $ from 'jquery';

export default class AcademyIntro extends Component {
    constructor(props) {
        super(props);
        this.state = {
            schools: [{ id: '1', name: 'loading...' }, { id: '2', name: 'loading...' }],
            levels: [{ id: '', name: 'loading...' }],
            departments: [{ id: '', name: 'loading...' }, { id: '2', name: 'loading...' }],

            category: 'jambites'
        };
    }

    componentDidMount() {
        // this.getSchools();
    }

    formChange = (e) => {
        this.setState({ [e.target.name]: e.target.value }); console.log(e.target.name);
    }

    getSchools = () => {
        // $('select').attr('disabled', true);
        const oldSchools = eng.getStorageJson('academy.schools');
        if (oldSchools.length > 0) {
            $('select').attr('disabled', false);
            this.setState({ schools: oldSchools });
        }
        // eng.getData('schools').then((res) => {
        //     console.log(res);
        //     $('select').attr('disabled', false);
        //     if (res.length > 0) {
        //         eng.setStorageJson('academy.schools', res);
        //         this.setState({ schools: res });
        //     }else{
        //         this.setState({ schools: [{ id: '', name: 'No schools found' }] });
        //     }
        // });
    }

    render() {
        const { schools, departments, levels } = this.state;
        const selected = eng.getStorageJson('academy.selected');
        const level = selected.level || 'ss';
        const school = selected.school || 'ss';
        const department = selected.department || 'ss';
        if (level && school && department) {
            return (
                <React.Fragment>
                    <div className="text-center page-header-title" style="padding: 140px 10px; background: url(/assets/img/academy-top.png);"></div>


                    <div id="content" className="container">
                        <div className="row">
                            <div className="col-lg-4 col-xs-12">
                                <div id="academy-enter-sidebar">
                                    SIDEBAR
            </div>
                            </div>
                            <div className="col-lg-8 col-xs-12">
                                <div className="text-center" style="background: #efefef;">
                                    <h3 style="margin: 0px; padding:15px;"><b>Course Introduction</b></h3>
                                </div>
                                <p>&nbsp;</p>
                                <p>Gain access to University of Lagos, UNILAG Computer Engineering 100 level COurse outline, exam past questions and answers, lesson notes and videos</p>

                                <p>&nbsp;</p>
                                <div>
                                    <div className="pointer" style="background: #ccc; padding: 15px; float:left; border-top-right-radius: 5px; border-top-left-radius: 5px; margin-right:5px;">Basic</div>
                                    <div className="pointer" style="background: #efefef; padding: 15px; float:left; border-top-right-radius: 5px; border-top-left-radius: 5px;">Advanced</div>
                                    <div className="clearfix"></div>
                                    <div style="background: #efefef; padding: 15px; border: 1px solid #ccc;">
                                        <div id="basic">

                                            <b style="display:block; border-bottom: 1px solid #fafafa; margin-bottom:3px;"><i className="fa fa-caret-down"></i> Chapter X</b>
                                            <ul>
                                                <li>Lorem ipsum</li>
                                                <li>Dolor sit amiet consecteur</li>
                                                <li>Adispiscing elit sed</li>
                                            </ul>

                                            <div>&nbsp;</div>
                                            <div className="text-right"><b>Price:</b> #100 &nbsp; <a href="javascript:;" onclick="$('#makePayment').modal('show');"><b>START COURSE <i className="fa fa-caret-right"></i></b></a></div>
                                        </div>
                                    </div>
                                </div>

                                <p>&nbsp;</p>

                                <div className="row">
                                    <div className="col-lg-4 col-md-4 col-sm-4 col-xs-12 m-b-lg">
                                        <img className="img-thumbnail no" src="/assets/img/articles/1.jpg" style="height: 150px;" />
                                    </div>
                                    <div className="col-lg-8 col-md-8 col-sm-8 col-xs-12 m-b-lg">
                                        <b>Meet The tutor</b>
                                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore .... &nbsp; &nbsp; <b><i>John Kenedy</i></b></p>
                                    </div>
                                </div>

                                <p>&nbsp;</p>
                                <h4><b>Positive Reviews From Students</b></h4>

                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea ..... &nbsp; &nbsp; <b><i>John Kenedy</i></b></p>
                            </div>
                            <div className="clearfix"></div>
                        </div>
                    </div>

                    <div id="academy-bottom">
                        <h1><b>Join Our Team</b></h1>
                        <h3>Do you want to earn <b>â‚¦30,000</b> monthly salary by taking an online campus job?</h3>
                        <p>&nbsp;</p>
                        <a href="/jointheteam" className="btn btn-md btn-warning"> Get Started Now </a>
                    </div>



                    <div id="makePayment" className="modal fade" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" data-backdrop="static" aria-hidden="true">
                        <div className="modal-dialog modal-md">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h4 className="modal-title mt-0" id="myModalLabel"><b>Fund Your Wallet To Continue</b></h4>
                                </div>
                                <div className="modal-body">
                                    <form action="/academy-enter-abc" autocomplete="off" method="post">
                                        <label className="pointer block" style="padding: 12px; background: #efefef;">
                                            <input type="radio" name="coursechossen" required value="1" /> Pay With Recharge Card
                    </label>
                                        <label className="pointer block" style="padding: 12px; background: #efefef;">
                                            <input type="radio" name="coursechossen" required value="2" /> Pay With Bank Card
                    </label>
                                        <hr />
                                        <button className="btn btn-sm btn-default pull-left" data-dismiss="modal">Close</button>
                                        <button className="btn btn-sm btn-primary pull-right">Enter</button>
                                        <div className="clearfix"></div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>

                </React.Fragment>
            );
        } else {
            return (
                <Redirect to="/" />
            );
        }

    }

}
