import React, { Component } from 'react';
import * as eng from '../providers/engine';

export default class List extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [], loading: true
        };
    }

    componentDidMount() {
        this.data();
    }

    data = () => {
        this.setState({ loading: true });
        eng.postData('sirjava&genesis=sirjava&case=list').then((res) => {
            this.setState({ loading: false, data: res.result });
        });
    }



    render() {
        const { data, loading } = this.state;
        return (
            <React.Fragment>
                <div className="container">
                    <p>&nbsp;</p>
                    <table className="table table-bordered table-striped">
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Ticket</th>
                                <th>Personal Details</th>
                                <th>Amount (GHc)</th>
                                <th>Status</th>
                                <th>Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {loading === true ? <tr><td colSpan="15" align="center">loading data...</td></tr> : ''}
                            {data.map(val => {
                                return <tr>
                                    <td>1</td>
                                    <td>{val.code}</td>
                                    <td>
                                        <b>Full name:</b> {val.firstname} {val.lastname} <br />
                                        <b>Max ID:</b> {val.maxid} <br />
                                        <b>Phone number:</b> {val.phone} <br />
                                        <b>Email:</b> {val.email}
                                    </td>
                                    <td>{val.amount}</td>
                                    <td>
                                        {val.status === '1' ?
                                            <span class="label label-success"><i className="fa fa-check"></i> PAID</span> :
                                            <span class="label label-warning"><i className="fa fa-close"></i> UNPAID</span>
                                        }
                                    </td>
                                    <td>{val.crdate}</td>
                                </tr>
                            })}
                        </tbody>
                    </table>
                </div>
            </React.Fragment>
        );
    }

}
