import React, { Component } from 'react';

export default class Select2 extends Component {
    componentDidMount() {
        let s = window.$('.select2');
        s.select2({
            change: this.formChange
        });
        s.on('change', this.props.onChange);
    }
    render() {
        const data = this.props.data ? this.props.data : [];
        const name = this.props.name ? this.props.name : '';
        const onlyShow = this.props.onlyShow ? this.props.onlyShow : [];
        const selected = this.props.selected ? this.props.selected : '';

        return (
            <React.Fragment>
                <select name={name} className="select2 form-control">
                    {/* {loading === true ? <option value="">loading...</option> : <option value="">Schools</option>} */}
                    {data.map(val => {
                        return <option key={val.id} value={val.id}>{val.name}</option>
                    })}
                </select>
            </React.Fragment>
        );
    }
}
