import React, { Component } from 'react';

export default class Select2 extends Component {
    componentDidMount() {
        let s = window.$('.select2');
        s.select2({
            change: this.formChange
        });
        s.on('change', this.props.onChange);
    }
    render() {
        const data = this.props.data;

        return (
            <React.Fragment>
                <select className="select2 form-control">
                    {/* {loading === true ? <option value="">loading...</option> : <option value="">Schools</option>} */}
                    {data.map(sch => {
                        return sch.id === '499' ? <option key={sch.id} value={sch.id}>{sch.name}</option> : ''
                    })}
                </select>
            </React.Fragment>
        );
    }
}
