import React, { Component } from 'react';
// import Select from 'react-select';
import * as eng from '../../providers/engine';
import $ from 'jquery';

export default class AcademyHome extends Component {
    constructor(props) {
        super(props);
        this.state = {
            schools: [{ id: '', name: 'loading...' }],
            levels: [{ id: '', name: 'loading...' }],
            departments: [{ id: '', name: 'loading...' }],

            category: 'jambites'
        };
    }

    skills = [1, 2, 3, 4, 5, 6, 7, 8];

    componentDidMount() {
        this.getSchools();
        // window.$('.select2').select2({
        //     change: this.formChange
        // });
    }

    formChange = (e) => {
        this.setState({ [e.target.name]: e.target.value }); console.log(e.target.name);
        if(e.target.name === 'department') {
            this.getDepartments();
        }
    }
    categoryChange = (category) => {
        this.setState({ category: category });
    }

    getSchools = () => {
        $('select').attr('disabled', true);
        const oldSchools = eng.getStorageJson('academy.schools');
        if (oldSchools.length > 0) {
            $('select').attr('disabled', false);
            this.setState({ schools: oldSchools });
        }
        eng.postData('schools').then((res) => {
            $('select').attr('disabled', false);
            if (res.length > 0) {
                eng.setStorageJson('academy.schools', res);
                // this.setState({ schools: res, departments:[], levels:[] });
            }else{
                this.setState({ schools: [{ id: '', name: 'No schools found' }] });
            }
        });
    }
    getDepartments = () => {
        let sch = $('').val(); console.log('get');
    }

    enterAcademy = () => {
        const { category } = this.state;
        let data = $('#' + category).find('input, select').serialize();
            data = eng.jsnData(data);
        eng.setStorageJson('academy.selected', data);
        this.props.history.push(`/intro`);
    }

    render() {
        const { schools, departments, levels } = this.state;
