import React, { Component } from 'react';
import * as eng from '../providers/engine';
import $ from 'jquery';

export default class Event extends Component {
    constructor(props) {
        super(props);
        this.state = {
            schools: [],
            levels: [],
            departments: [],

            category: 'jambites',

            loading: true
        };
    }

    skills = [1, 2, 3, 4, 5, 6, 7, 8];

    componentDidMount() {
        this.getSchools();
    }

    formChange = (e) => {
        // this.setState({ [e.target.name]: e.target.value });
    }
    categoryChange = (category) => {
        this.setState({ category: category });
    }

    getSchools = () => {
        $('select').attr('disabled', true);
        this.setState({ loading: true });
        const oldSchools = eng.getStorageJson('academy.schools');
        if (oldSchools.length > 0) {
            $('select').attr('disabled', false);
            this.setState({ loading: false });
            this.setState({ schools: oldSchools });
        }
        eng.getData('schools').then((res) => {
            $('select').attr('disabled', false);
            this.setState({ loading: false });
            if (res.length > 10) {
                eng.setStorageJson('academy.schools', res);
                this.setState({ schools: res });
            } else {
                this.setState({ schools: [{ id: '', name: 'No schools found' }] });
            }
        });
    }

    enterAcademy = () => {
        const { category } = this.state;
        let data = $('#' + category).find('input, select').serialize();
        data = eng.jsnData(data);
        eng.setStorageJson('academy.selected', data);
        this.props.history.push(`/intro`);
    }
    // RS_BE18F19081347BD3DA186ACF6C903EBF

    render() {
        const { loading, schools, departments, levels } = this.state;
        return (
            <React.Fragment>
                <div className="container">
                    <img className="img-thumbnail" src="/assets/img/banner.jpg" alt="Leadership Summit 2019 - SirJava Official" />
                </div>
            </React.Fragment>
        );
    }

}
