import React, { Component } from 'react';
import * as eng from '../providers/engine';
import $ from 'jquery';

export default class Event extends Component {
    constructor(props) {
        super(props);
        this.state = {
            schools: [],
            levels: [],
            departments: [],

            category: 'jambites',

            loading: true
        };
    }

    formChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    submit = (e) => {
        e.preventDefault();
        // $('select').attr('disabled', true);
        // this.setState({ loading: true });
        // const oldSchools = eng.getStorageJson('academy.schools');
        // if (oldSchools.length > 0) {
        //     $('select').attr('disabled', false);
        //     this.setState({ loading: false });
        //     this.setState({ schools: oldSchools });
        // }
        // eng.getData('schools').then((res) => {
        //     $('select').attr('disabled', false);
        //     this.setState({ loading: false });
        //     if (res.length > 10) {
        //         eng.setStorageJson('academy.schools', res);
        //         this.setState({ schools: res });
        //     } else {
        //         this.setState({ schools: [{ id: '', name: 'No schools found' }] });
        //     }
        // });
    }
    // RS_BE18F19081347BD3DA186ACF6C903EBF

    render() {
        const { loading, schools, departments, levels } = this.state;
        return (
            <React.Fragment>
                <div className="overlay">
                    <div className="containers" style={{ padding: 0 }}>
                        <img className="banner img-thumbnail no" src="/assets/img/banner.jpg" alt="Leadership Summit 2019 - SirJava Official" />
                    </div>
                </div>

                <div id="venu">
                    <div className="container">
                        <div className="row">
                            <div className="col-2 col-lg-2"></div>
                            <div className="col-4 col-lg-4">
                                <div className="pull-left">
                                    <h3><i className="fa fa-map-marker fa-3x"></i></h3>
                                </div>
                                <div className="pull-left" style={{ paddingLeft: 15 }}>
                                    <h3><b>College of Science</b></h3>
                                    <p className="text-muted">Auditorium KNUST Kumasi</p>
                                </div>
                            </div>
                            <div className="col-4 col-lg-4">
                                <div className="pull-left">
                                    <h3><i className="fa fa-calendar fa-3x"></i></h3>
                                </div>
                                <div className="pull-left" style={{ paddingLeft: 15 }}>
                                    <h3><b>Tuesday 6th July</b></h3>
                                    <p className="text-muted">Events starts @ 8:30am</p>
                                </div>
                            </div>
                            <div className="col-2 col-lg-2"></div>
                        </div>
                    </div>
                </div>

                <div className="overlay">
                    <div className="container" style={{ background: '#fff', padding: 30, minHeight: 800 }}>
                        <div className="text-center">
                            <h1>Kumasi Leadership Conference</h1>
                            <div className="box yellow">
                                Exclusive to Max International Associates. 600 Seats Limited
                            </div>
                        </div>


                        {/* F O R M */}
                        <p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>
                        <div className="row">
                            <div className="col-12 text-center">
                                <h3>Reserve your ticket here</h3>
                                <p>&nbsp;</p>
                            </div>
                            <div className="col-3 col-lg-3"></div>
                            <div className="col-12 col-lg-6">
                                <form id="form" method="post" autoComplete="off" autoCorrect="off" action="/" onSubmit={this.submit}>
                                    <div className="form-group">
                                        <input type="text" className="form-control form-control-lg" required placeholder="Enter your First name" />
                                    </div>
                                    <div className="form-group">
                                        <input type="number" className="form-control form-control-lg" required placeholder="Enter your Max ID" />
                                    </div>
                                    <div className="form-group">
                                        <input type="email" className="form-control form-control-lg" required placeholder="Enter your Email address" />
                                    </div>
                                    <div className="form-group">
                                        <input type="number" className="form-control form-control-lg" required placeholder="Enter your Mobile number with country code" />
                                    </div>
                                    <div className="form-group text-center">
                                        <button className="btn btn-lg btn-warning btn-block">RESERVE</button>
                                        <small className="text-muted">* I understand the seats are limited</small>
                                    </div>
                                </form>
                            </div>
                            <div className="col-3 col-lg-3"></div>
                        </div>



                        <p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>
                        <div className="row">
                            <div className="col-12 text-center">
                                <h3>TOPICS TO BE TREATED</h3>
                                <p>&nbsp;</p>
                            </div>
                            <div className="col-2 col-lg-2"></div>
                            <div className="col-4 col-lg-4">
                                <ul>
                                    <li>What We do Inside of Network Marketing</li>
                                    <li>Team &amp; Business Investments</li>
                                    <li>Handling Objects on Product Sales</li>
                                    <li>Confidence &amp; Self Esteem</li>
                                    <li>The Journey of Personal Development</li>
                                </ul>
                            </div>
                            <div className="col-4 col-lg-4">
                                <ul>
                                    <li>Performance &amp; Productivity</li>
                                    <li>Responsibility &amp; Commitment to Business Growth</li>
                                    <li>Emotional Intelligence (Attitude and Mindset)</li>
                                    <li>Handling Objections as a Leader</li>
                                </ul>
                            </div>
                            <div className="col-2 col-lg-2"></div>
                        </div>
                    </div>
                </div>

            </React.Fragment>
        );
    }

}
