import React, { Component } from 'react';
import Select2 from '../components/select2';
import * as eng from '../../providers/engine';
import $ from 'jquery';

export default class AcademyHome extends Component {
    constructor(props) {
        super(props);
        this.state = {
            schools: [],
            levels: [],
            departments: [],

            category: 'jambites',

            loading: true
        };
    }

    skills = [1, 2, 3, 4, 5, 6, 7, 8];

    componentDidMount() {
        this.getSchools();
    }

    formChange = (e) => {
        this.setState({ [e.target.name]: e.target.value }); console.log(e.target.name);
        switch (e.target.name) {
            case 'school':
                this.getDepartments();
            case 'department':
            // this.getLevels();
        }
    }
    categoryChange = (category) => {
        this.setState({ category: category });
    }

    getSchools = () => {
        $('select').attr('disabled', true);
        this.setState({ loading: true });
        const oldSchools = eng.getStorageJson('academy.schools');
        if (oldSchools.length > 0) {
            $('select').attr('disabled', false);
            this.setState({ loading: false });
            this.setState({ schools: oldSchools });
        }
        eng.getData('schools').then((res) => {
            $('select').attr('disabled', false);
            this.setState({ loading: false });
            if (res.length > 10) {
                eng.setStorageJson('academy.schools', res);
                this.setState({ schools: res });
            } else {
                this.setState({ schools: [{ id: '', name: 'No schools found' }] });
            }
        });
    }
    getDepartments = () => {
        $('select').attr('disabled', true);
        this.setState({ loading: true });
        const { school } = this.state;
        const oldDepartments = eng.getStorageJson('academy.departments.' + school);
        if (oldDepartments.length > 0) {
            $('select').attr('disabled', false);
            this.setState({ loading: false });
            this.setState({ departments: oldDepartments });
        }
        eng.getData('schools&case=departments&school_id=' + school).then((res) => {
            $('select').attr('disabled', false);
