import React, { Component } from 'react';
import { Route, Redirect } from 'react-router-dom';

import * as eng from './providers/engine';

import Event from './pages/event';

export default class SirJava extends Component {
    constructor(props) {
        super(props);
        this.state = {
            msg: '', status: ''
        };
    }

    params = this.props.match.params;

    render() {
        const action = this.params.action ? this.params.action : '';
        eng.setStorageJson('route', this.params);

        const { status, msg } = this.state;

        return (
            <React.Fragment>
                {/* <Header /> */}
                {eng.alertMsg(status, msg)}
                <Route exact path="/" component={Event} />
                <Route exact path="/:action" component={Event} />
                {/* <Footer /> */}
            </React.Fragment>
        );
    }
}
