import React, { Component } from 'react';
import * as eng from '../../providers/engine';

export default class Select2 extends Component {
    componentDidMount() {
        let s = window.$('.select2');
        s.select2({
            change: this.formChange
        });
        s.on('change', this.props.onChange.bing(this));
    }
    render() {
        const data = this.props.data ? this.props.data : [];
        const name = this.props.name ? this.props.name : '';
        const label = this.props.label ? this.props.label : '';
        const loading = this.props.loading ? this.props.loading : false;
        const onlyShow = this.props.onlyShow ? this.props.onlyShow : '';
        const selected = this.props.selected ? this.props.selected : '';

        if(onlyShow) {
            return (
                <React.Fragment>
                    <select name={name} className="select2 form-control">
                        {/* {loading === true ? <option value="">loading...</option> : <option value="">{label}</option>} */}
                        <option value="">{label}</option>
                        {data.map(val => {
                            return val.id === onlyShow ? <option key={val.id} value={val.id}>{val.name}</option> : ''
                        })}
                    </select>
                </React.Fragment>
            );
        }else {
            return (
                <React.Fragment>
                    <select name={name} className="select2 form-control">
                        {/* {loading === true ? <option value="">loading...</option> : <option value="">{label}</option>} */}
                        <option value="">{label}</option>
                        {data.map(val => {
                            return <option key={val.id} value={val.id}>{val.name}</option>
                        })}
                    </select>
                </React.Fragment>
            );
        }
    }
}
