import React, { Component } from 'react';
import * as eng from '../../providers/engine';
import $ from 'jquery';

// declare var showToast: any;

export default class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            pin: '', password: '',
        };
    }

    formChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    __login = (e) => {
        e.preventDefault();
        $('form').find('input, select, textarea').attr('disabled', true);
        $('#submit').html('<i class="fa fa-spin fa-spinner"></i> authenticating ...').attr('disabled', true);
        eng.postData('authenticate&genesis=authenticate&case=login', this.state).then((res) => {
            $('#submit').html('SIGN IN').attr('disabled', false);
            $('form').find('input, select, textarea').attr('disabled', false);
            if (res.status === 200) {
                $('#submit').html('Redirecting...');
                eng.alertMsgSet('success', 'Welcome back ' + res.data.name + '!');
                eng.setStorage('userToken', res.token);
                eng.setStorageJson('userData', res.data);
                eng.redirect('/');
            } else {
                // showToast('warning', res.result);
            }
        });
    }

    render() {
        return (
            <React.Fragment>
                <div className="container" style={{ marginTop: 25 + 'px' }}>
                    <div className="row">
                        <div className="col-sm-5" style={{ float: 'none', margin: 'auto' }}>
                            <div id="mini-content" style={{ minHeight: 'auto' }}>
                                <div className="text-center">
                                    <h2>Campus<b>Punch</b>.</h2>
                                    <div className="text-muted">Login to your account <br /> {/* - OR - <br /> <button className="btn btn-primary btn-block"><i className="fa fa-facebook-square"></i> Signin with Facebook</button> */}</div>
                                </div>
                                <p>&nbsp;</p>
                                <form autocomplete="off" name="signin" method="post" action="#">
                                    <div className="form-group">
                                        <label>Username/Email</label>
                                        <input type="text" className="form-control" name="name" />
                                    </div>
                                    <div className="form-group">
                                        <label>Password</label>
                                        <input type="password" className="form-control" name="name" />
                                    </div>
                                    <div className="form-group">
                                        <label className="pointer">
                                            <input type="checkbox" value="1" /> &nbsp;
                                            Remember me</label>
                                    </div>
                                    <div className="form-group">
                                        <div className="row">
                                            <div className="col-sm-2"></div>
                                            <div className="col-sm-8">
                                                <button className="btn btn-primary btn-blockc">Login</button> &nbsp;
                                                <a href="/forgot" className="btn btn-default btn-blockc">Forgot password</a> </div>
                                            <div className="col-sm-2"></div>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <p>&nbsp;</p>
                            <div className="text-center"> Don't have an account? <a href="/signup"><b>Sign up</b></a> now! </div>
                            <p>&nbsp;</p><p>&nbsp;</p>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }

}
